import React, { Component } from 'react';
import socketIOClient from 'socket.io-client';
import LoadingDots from './components/LoadingDots';
import InputAndSubmitField from './components/InputAndSubmitField';

const socket = socketIOClient('http://127.0.0.1:4000');


class App extends Component {
	state = {
		messages: [],
		message: '',
		name: '',
		id: '',
		location: {},
		loading: true,
		userSubmitted: false
	};

	componentDidMount() {
		navigator.geolocation.getCurrentPosition(position => {
			console.log('location found')
			this.setState({
				location: {
					lon: position.coords.longitude,
					lat: position.coords.latitude
				},
				loading: false
			})
		});

		socket.on('connect', () => {
			this.setState({id: socket.id});
		})
		// socket.emit('create', prompt());
		socket.on('chat message', msg => {
			this.setState(previousState => ({
			    messages: [...previousState.messages, msg]
			}));
		});
	}

	setName = e => {
		this.setState({ name: e.target.value })
	}

	submitUser = async e => {
		e.preventDefault()
		this.setState({userSubmitted: true});
		if (this.state.name !== ''){
			try {
				await fetch('http://127.0.0.1:4000/test', {
					method: 'POST',
					headers: {
						'Accept': 'application/json',
						'Content-Type': 'application/json',
					},
					body: JSON.stringify({
						name: this.state.name,
						id: this.state.id,
						location: {
							type: "Point",
							coordinates: [this.state.location.lon, this.state.location.lat]
						}
					})
				})
			} catch(err) {
				console.log(err)
			}
		}
	}

  render() {
		const { loading, userSubmitted } = this.state;
    return (
			<div>
				{loading &&
					<LoadingDots />}
				{!loading && 
					<InputAndSubmitField
						placeholder="Enter your name"
						name={this.state.name}
						setName={this.setName}
						submitUser={this.submitUser}
					/>}
			</div>
			// 	<form>
			// 		<input type="text" value={this.state.message} onChange={ ({target}) => this.setState({message: target.value}) } />
			// 		<button type="submit" onClick={e => {
			// 			e.preventDefault();
			// 			socket.emit('chat message', this.state.message);
			// 			this.setState({message: ""})
			// 		}} />
			// 	</form>
		)
  }
}

export default App;
