import React, { Component } from 'react';
import socketIOClient from 'socket.io-client';
const socket = socketIOClient('http://127.0.0.1:4000');


class App extends Component {
	state = {
		messages: [],
		message: '',
		name: '',
		id: '',
		location: {}
	};

	componentDidMount() {
		navigator.geolocation.watchPosition(function () {}, function () {}, {});
			//The working next statement.
			navigator.geolocation.getCurrentPosition( position => {
				console.log(position)
				this.setState({
					location: {
						lon: position.coords.longitude,
						lat: position.coords.latitude
					}
				})
			}, function (err) {
			    console.log(err)
			}, {
			    enableHighAccuracy: true
			});
		// navigator.geolocation.getCurrentPosition(position => {
		// 	console.log('SUCCESS ', position);
		// 	this.setState({
		// 		location: {
		// 			lon: position.coords.longitude,
		// 			lat: position.coords.latitude
		// 		}
		// 	})
		// }, err => {
		// 	console.log('ERROR ', err);
		// }, {timeout: 30000, maximumAge: 0});
		socket.on('connect', () => {
			this.setState({id: socket.id});
		})
		// socket.emit('create', prompt());
		socket.on('chat message', msg => {
			this.setState(previousState => ({
			    messages: [...previousState.messages, msg]
			}));
		});
	}

  render() {
    return (
    	<div>
				<div>
					<h1>{this.state.name}</h1>
					<h1>{this.state.id}</h1>
					<h1>Location:</h1>
					<h2>Longitude</h2>
					{this.state.location.lon}
					<h2>Latitude</h2>
					{this.state.location.lat}
				</div>
				<br />
    		<div>
    			{this.state.messages}
    		</div>
				<form>
					<input type="text" value={this.state.message} onChange={ ({target}) => this.setState({message: target.value}) } />
					<button type="submit" onClick={e => {
						e.preventDefault();
						socket.emit('chat message', this.state.message);
						this.setState({message: ""})
					}} />
				</form>
			</div>
		)
  }
}

export default App;
