import React from 'react';
import styled from 'styled-components';

const StyledInputAndSubmitField = styled.div`
  position: absolute;
  height: 50px;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  border-radius: 5px;
  overflow: hidden;
  input {
    border: none;
    height: 100%
    padding: 0 10px;

  }
  div {
    position: relative;
		display: inline;
		float: right;
		height: 100%;
    width: 50px;
		background-color: #34495e;
  }
`

const InputAndSubmitField = props => {
  return(
    <StyledInputAndSubmitField>
      <input placeholder={props.placeholder} default='input name' type="text" value={props.default} onChange={props.setName} />
      <div onClick={e => {

      }} ></div>
    </StyledInputAndSubmitField>
  )
}

export default InputAndSubmitField;
