const initialState = {
  name: 'Sheridan',
  rooms: ['room1', 'room2', 'room3'],
  activeRoom: 0
}

const reducer = (state = initialState, action) => {
  switch(action.type) {
    case 'UPDATE_NAME':
      return Object.assign({}, state, {
        name: action.name
      })
     case 'UPDATE_ACTIVE_ROOM':
        return Object.assign({}, state, {
          activeRoom: action.activeRoom
        })
     default:
      return state
  }
}

export default reducer
