import React from 'react';
import styled from 'styled-components';


const TabBar = ({rooms, activeRoom, updateActiveRoom, deleteRoom}) => {


  const roomTabs = rooms.map((room, index) => {
    if(index == activeRoom.index){
      return (
        <div index={index} style={{backgroundColor: 'yellow'}}>
          {room}
          <button onClick={deleteRoom(index)} />
        </div>
      )
    } else {
      return (
        <div index={index} onClick={() => {updateActiveRoom(room, index)}}>
          {room}
          <button onClick={deleteRoom(index)} />
        </div>
      )
    }
  })
  return (
    <div>{roomTabs}</div>
  )
}

export default TabBar;
