import React from 'react';
import styled from 'styled-components';


const TabBar = ({rooms, activeRoom, updateActiveRoom, deleteRoom}) => {


  const roomTabs = rooms.map((room, index) => {
    if(index == activeRoom.index){
      return (
        <span>
          <span index={index} style={{backgroundColor: 'yellow'}}>
            {room}
          </span>
          <button onClick={() => deleteRoom(2)}/>
        </span>
      )
    } else {
      return (
        <span>
          <span index={index} onClick={() => updateActiveRoom(room, index)}>
            {room}
          </span>
          <button onClick={() => deleteRoom(2)}/>
        </span>
      )
    }
  })
  return (
    <div>{roomTabs}</div>
  )
}

export default TabBar;
