import React from 'react';
import styled from 'styled-components';



class TabBar extends React.Component {
  state = {
    tabs: ['1', '2', '3', '4'],
    activeTab: ''
  }

  setActiveTab = index => {
    this.setState({activeTab: index})
  }

  render() {
    const tabs = this.state.tabs.map((tab, index) => {
      if(index == activeTab){
        return (
          <span index={index} style={{backgroundColor: 'yellow'}}>
            {tab}
          </span>
        )
      } else {
        return (
          <span index={index} onClick={() => {this.setActiveTab(index)}}>
            {tab}
          </span>
        )
      }
    })
    return (
      <div>{tabs}</div>
    )
  }
}

export default TabBar;
