import React from 'react';
import styled from 'styled-components';


const TabBar = ({rooms, activeRoom, updateActiveRoom, deleteRoom}) => {


  const roomTabs = rooms.map((room, index) => {
    if(index == activeRoom.index){
      return (
        <span>
          <span index={index} style={{backgroundColor: 'yellow'}}>
            {room}
          </span>
          <button onClick={() => {
            if(rooms.length == 1){
              updateActiveRoom('', '')
            } else if(index == rooms.length-1) {
              updateActiveRoom(rooms[index-1], index-1)
            } else {
              updateActiveRoom(rooms[index+1], index)
            }
            deleteRoom(index);
          }}/>
        </span>
      )
    } else {
      return (
        <span>
          <span index={index} onClick={() => updateActiveRoom(room, index)}>
            {room}
          </span>
          <button onClick={() => {
            if(false){
              updateActiveRoom()
            }
            deleteRoom(index);
          }}/>
        </span>
      )
    }
  })
  return (
    <div>{roomTabs}</div>
  )
}

export default TabBar;
