import React from 'react';
import styled from 'styled-components';

const StyledInputAndSubmitField = styled.div`
  position: absolute;
  display: flex;
  height: 50px;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  border-radius: 5px;
  overflow: hidden;
  input {
    border: none;
    height: 100%
    width: 200px;
    padding: 0 10px;
    font-family: "Geneva";
    font-size: 1em;
  }
  button {
    display: flex;
		height: 100%;
    width: 70px;
		background-color: #34495e;
    border: none;
    font-size: 1em;
    :hover {
      background-color: #425ff4;
    }
    :active{
      background-color: #34495e;
    }
    span{
      text-align: center;
      margin: auto;
      color: white;
      padding: 0 5px;
      font-family: Geneva;
    }
  }
`

const InputAndSubmitField = props => {
  return(
    <StyledInputAndSubmitField>
      <input placeholder={props.placeholder} type="text" value={props.default} onChange={props.setName} />
      <form>
      <button onClick={props.submitUser} >
          <span>Send</span>
        </button>
      </form>
    </StyledInputAndSubmitField>
  )
}

export default InputAndSubmitField;
